import React, { useState, useMemo } from "react";
import styled from "styled-components";
import { useGlobalContext } from "../../context/globalContext";
import { InnerLayout } from "../../styles/Layouts";
import { Pie } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

// Register Chart.js components
ChartJS.register(ArcElement, Tooltip, Legend);

function ViewTransactions() {
  const { incomes, expenses } = useGlobalContext();
  const [filter, setFilter] = useState("all");

  // Combine incomes and expenses with type
  const allTransactions = useMemo(
    () => [
      ...incomes.map((item) => ({ ...item, type: "Income" })),
      ...expenses.map((item) => ({ ...item, type: "Expense" })),
    ],
    [incomes, expenses]
  );

  // Filter transactions
  const filteredTransactions = useMemo(() => {
    if (filter === "all") return allTransactions;
    return allTransactions.filter((t) => t.type.toLowerCase() === filter);
  }, [filter, allTransactions]);

  // Pie chart data
  const pieChartData = useMemo(() => {
    if (filter === "all") {
      const totalIncome = incomes.reduce((sum, item) => sum + item.amount, 0);
      const totalExpense = expenses.reduce((sum, item) => sum + item.amount, 0);
      return {
        labels: ["Income", "Expenses"],
        datasets: [
          {
            data: [totalIncome, totalExpense],
            backgroundColor: ["#34C759", "#FF3B30"], // Green for income, red for expenses
            borderColor: ["#FFFFFF", "#FFFFFF"],
            borderWidth: 2,
          },
        ],
      };
    }

    // For 'income' or 'expense', group by category
    const isIncome = filter === "income";
    const transactions = isIncome ? incomes : expenses;
    const categoryMap = transactions.reduce((acc, item) => {
      const category =
        item.category || item.title || (isIncome ? "Income" : "Expense");
      acc[category] = (acc[category] || 0) + item.amount;
      return acc;
    }, {});

    // Generate shades of green for income, red for expenses
    const baseColor = isIncome ? [52, 199, 89] : [255, 59, 48]; // RGB for #34C759 or #FF3B30
    const colors = Object.keys(categoryMap).map((_, index) => {
      const factor = 1 - index * 0.1; // Lighten/darken by 10% per category
      return `rgb(${baseColor[0] * factor}, ${baseColor[1] * factor}, ${
        baseColor[2] * factor
      })`;
    });

    return {
      labels: Object.keys(categoryMap),
      datasets: [
        {
          data: Object.values(categoryMap),
          backgroundColor: colors,
          borderColor: ["#FFFFFF"],
          borderWidth: 2,
        },
      ],
    };
  }, [filter, incomes, expenses]);

  // Pie chart options
  const pieChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: "bottom",
        labels: {
          font: {
            size: 14,
          },
        },
      },
      tooltip: {
        callbacks: {
          label: (context) => {
            const label = context.label || "";
            const value = context.raw || 0;
            return `${label}: $${value.toLocaleString()}`;
          },
        },
      },
    },
  };

  // Handle filter changes from TransactionFilter
  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  return (
    <ViewTransactionsStyled>
      <InnerLayout>
        <h1>Recent Transactions</h1>
        <TransactionFilter onFilterChange={handleFilterChange} />
        <div className="chart-wrapper">
          <h2>Transaction Breakdown</h2>
          <div className="pie-chart">
            <Pie data={pieChartData} options={pieChartOptions} />
          </div>
        </div>
      </InnerLayout>
    </ViewTransactionsStyled>
  );
}

const ViewTransactionsStyled = styled.div`
  .chart-wrapper {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 1.5rem;
    background: #fcf6f9;
    border: 2px solid #ffffff;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    border-radius: 20px;
    margin-top: 2rem;

    h2 {
      font-size: clamp(1.2rem, 2.5vw, 1.5rem);
      margin-bottom: 1rem;
      text-align: center;
    }

    .pie-chart {
      width: 100%;
      max-width: 400px;
      height: 300px;
      position: relative;
    }
  }

  @media (max-width: 768px) {
    .chart-wrapper {
      padding: 1rem;

      .pie-chart {
        max-width: 300px;
        height: 250px;
      }
    }
  }

  @media (max-width: 480px) {
    .chart-wrapper {
      padding: 0.8rem;

      h2 {
        font-size: clamp(1rem, 3vw, 1.2rem);
      }

      .pie-chart {
        max-width: 250px;
        height: 200px;
      }
    }
  }
`;

export default ViewTransactions;
