import React, { useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

function TransactionFilter({ onFilterChange }) {
    const { incomes, expenses } = useGlobalContext();
    const [filter, setFilter] = useState('all');

    // Combine incomes and expenses for display
    const allTransactions = [
        ...incomes.map(item => ({ ...item, type: 'Income' })),
        ...expenses.map(item => ({ ...item, type: 'Expense' })),
    ];

    // Filter transactions based on selected filter
    const filteredTransactions = filter === 'all'
        ? allTransactions
        : allTransactions.filter(transaction => transaction.type.toLowerCase() === filter);

    // Call the parent component's callback with filtered transactions
    const handleFilterChange = (newFilter) => {
        setFilter(newFilter);
        onFilterChange(filteredTransactions.filter(t => t.type.toLowerCase() === newFilter || newFilter === 'all'));
    };

    return (
        <FilterStyled>
            <h3>Filter Transactions</h3>
            <div className="filter-buttons">
                <button
                    className={filter === 'all' ? 'active' : ''}
                    onClick={() => handleFilterChange('all')}
                >
                    All
                </button>
                <button
                    className={filter === 'income' ? 'active' : ''}
                    onClick={() => handleFilterChange('income')}
                >
                    Income
                </button>
                <button
                    className={filter === 'expense' ? 'active' : ''}
                    onClick={() => handleFilterChange('expense')}
                >
                    Expenses
                </button>
            </div>
            <div className="transaction-list">
                {filteredTransactions.length > 0 ? (
                    filteredTransactions.map((transaction, index) => (
                        <div key={index} className="transaction-item">
                            <p>{transaction.title || transaction.type}</p>
                            <p className={transaction.type.toLowerCase()}>
                                ${transaction.amount.toLocaleString()}
                            </p>
                        </div>
                    ))
                ) : (
                    <p>No transactions found.</p>
                )}
            </div>
        </FilterStyled>
    );
}

const FilterStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    border-radius: 20px;
    padding: 1.5rem;
    margin: 2rem 0;

    h3 {
        font-size: clamp(1rem, 2vw, 1.2rem);
        margin-bottom: 1rem;
    }

    .filter-buttons {
        display: flex;
        gap: 1rem;
        margin-bottom: 1.5rem;

        button {
            background: #FFFFFF;
            border: 1px solid #E0E0E0;
            border-radius: 10px;
            padding: 0.5rem 1rem;
            font-size: clamp(0.9rem, 1.5vw, 1rem);
            cursor: pointer;
            transition: all 0.3s ease;

            &.active {
                background: #34C759;
                color: #FFFFFF;
                border-color: #34C759;
            }

            &:hover {
                background: #F0F0F0;
            }
        }
    }

    .transaction-list {
        max-height: 300px;
        overflow-y: auto;
        padding-right: 0.5rem;

        .transaction-item {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 0.8rem;
            border-bottom: 1px solid #E0E0E0;
            font-size: clamp(0.9rem, 1.5vw, 1rem);

            &:last-child {
                border-bottom: none;
            }

            p.income {
                color: #34C759;
            }

            p.expense {
                color: #FF3B30;
            }
        }
    }

    @media (max-width: 768px) {
        padding: 1rem;

        .filter-buttons {
            flex-wrap: wrap;
            gap: 0.8rem;
        }

        .transaction-list {
            max-height: 250px;
        }
    }

    @media (max-width: 480px) {
        h3 {
            font-size: clamp(0.9rem, 2.5vw, 1rem);
        }

        .filter-buttons {
            button {
                padding: 0.4rem 0.8rem;
                font-size: clamp(0.8rem, 2vw, 0.9rem);
            }
        }

        .transaction-list {
            max-height: 200px;
        }
    }
`;

export default TransactionFilter;
